import React, { useState, useEffect } from 'react'
import Card from './Card'
import Controls from './Controls'
import logo from '../img/back-of-card.png'

const Game = (props) => {
  const [deck, setDeck] = useState([])

  useEffect( () => {
    fetch("https://deckofcardsapi.com/api/deck/new/draw/?count=52")
    .then(response => {
      if(response.ok){
        return response
      } else{
        let errorMessage = `${response.status}(${response.statusText})`,
        error = new Error(errorMessage)
        throw(error)
      }
    })
    .then(response => {
      return response.json()
    })
    .then(body => {
      setDeck(body)
    })
    .catch(error => console.error(`Error in fetch: ${error.message}`))
  }, [])

  let cards

  // if (deck.cards) {
  //   cards = deck.cards.map((card) => {
  //     return(
  //       <Card
  //         key = {card.id}
  //         id = {card.id}
  //         value = {card.value}
  //         suit = {card.suit}
  //         image = {card.image}
  //       />
  //     )
  //   })
  // }

  const cardPlayed = document.getElementById("card-played")

  const drawCard = event => {
    if (deck.cards) {
    fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=1`)
    .then(card => {
      cardPlayed.innerHTML = "hello"
    })
  }
}

  return(
    <div>
      <div id="card-stack">
        <img src={logo}></img>
      </div>
      <Controls drawCard={drawCard}/>
      <div id="card-played">
        {cards}
      </div>
      {/* <Controls drawCard={drawCard} /> */}
    </div>
  )
}

export default Game
