import React, { useState, useEffect } from 'react'
import logo from '../img/back-of-card.png'
import { getValue } from '../helpers/helpers'

const Game = (props) => {
  const [deck, setDeck] = useState({})
  const [cardsPlayed, setCardsPlayed] = useState()

  let compCardPlayedValue
  let userCardPlayedValue

  const compCardPlayed = document.getElementById("comp-card-played")
  const userCardPlayed = document.getElementById("user-card-played")

  const compDiscardStack = document.getElementById("comp-discard-stack")
  const userDiscardStack = document.getElementById("user-discard-stack")

  useEffect( () => {
    fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')
    .then(data => data.json())
    .then(cards => {
      setDeck(cards)
    })
  }, [])

  if (deck.remaining > 0) {
    fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=1`)
    .then(data => data.json())
    .then(card => {
      if(card.cards){
        compCardPlayed.innerHTML = `<img src=${card.cards[0].image}></img>`
        compCardPlayedValue = getValue(card.cards[0].value)
      }
      deck.remaining -= 1
    })
  }

  const drawCard = event => {
    if (deck.remaining > 0) {
      fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=2`)
      .then(data => data.json())
      .then(card => {

        // User plays card
        if(card.cards){
          userCardPlayed.innerHTML = `<img src=${card.cards[0].image}></img>`
          userCardPlayedValue = getValue(card.cards[0].value)
        }
        deck.remaining -= 1

        // Decide who wins hand
        if(userCardPlayedValue > compCardPlayedValue){
          // User wins
          // Add comp/user cards to users pile
          userDiscardStack.innerHTML = `<img src=${card.cards[0].image}></img>`
          // 
        } else if(userCardPlayedValue < compCardPlayedValue){
            // Computer wins
            console.log("You lose!")
        } else{
            // War!
            console.log("War!")
        }

        // Comp plays new card
        // setTimeout(function(){
        //   compCardPlayed.innerHTML = `<img src=${card.cards[0].image}></img>`
        //   compCardPlayedValue = getValue(card.cards[0].value)
        // }
        // , 2000)
      })
    }else{
      userCardPlayed.innerHTML = "Out of cards"
    }
  }

  return(
    <div>
      <div className="grid-x grid-margin-y">
        <div className="cell small-4"></div>
        <div className="cell small-2 card-stack" id="comp-card-stack">
          <img src={logo}></img>
        </div>
        <div className="cell small-2 card-stack" id="comp-discard-stack"></div>
        <div className="cell small-4"></div>
      </div>

      <div className="grid-x grid-margin-y grid-padding-y">
        <div className="cell small-4"></div>
        <div className="cell small-2">
          <div className="card-played" id="comp-card-played"></div>
        </div>
        <div className="cell small-2">
          <div className="card-played" id="user-card-played"></div>
        </div>
        <div className="cell small-4"></div>
      </div>

      <div className="grid-x grid-margin-y">
        <div className="cell small-4"></div>
        <div className="cell small-2 card-stack" id="user-discard-stack"></div>
        <div className="cell small-2 card-stack" id="user-card-stack" onClick={drawCard}>
          <img src={logo}></img>
        </div>
        <div className="cell small-4"></div>
      </div>
    </div>
  )
}

export default Game
