import React, { useState, useEffect } from 'react'
import backOfCard from '../img/back-of-card.png'
import backOfCardUpsideDown from '../img/back-of-card-upside-down.png'
import { getValue } from '../helpers/helpers'
import ship from '../img/ship.png'

const Game = (props) => {
  let compCardPlayedValue
  let userCardPlayedValue

  let compCardCodes = ""
  let userCardCodes = ""

  const compDiscardStack = document.getElementById("comp-discard-stack")
  const userDiscardStack = document.getElementById("user-discard-stack")

  const compCardPlayed = document.getElementById("comp-card-played")
  const userCardPlayed = document.getElementById("user-card-played")
  const [deck, setDeck] = useState({})
  const [cardsPlayed, setCardsPlayed] = useState()

  // Create/shuffle deck
  useEffect( () => {
    fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')
    .then(response => response.json())
    .then(deck => {
      fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=52`)
      .then(response => response.json())
      .then(drawn => {
        let counter = 0
        drawn.cards.forEach(card => {
          if(counter % 2 === 0){
            compCardCodes += `${card.code},`
            counter += 1
          } else{
            userCardCodes += `${card.code},`
            counter += 1
          }
        })
        return drawn
      })
      .then(deck => {
        fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/pile/compPile/add/?cards=${compCardCodes}`)
        .then(response => response.json())
        .then(pile => {
          setDeck(pile)
        })
        return deck
      })
      .then(deck => {
        fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/pile/userPile/add/?cards=${userCardCodes}`)
        .then(response => response.json())
        .then(pile => {
          setDeck(pile)
        })
      })
    })
    // fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/pile/gamePile/add/?count=26`)
    // .then(response => response.json())
    // .then(pile => {
    //   console.log(pile)
    //   debugger
    // })
    // setDeck(data)

    if(document.getElementsByClassName("userName")){
      let userNameInput = prompt("What's your name?")
      debugger
      // if(userNameInput === "")
      let userNames = document.getElementsByClassName("userName")
      for(let i of userNames){
        i.innerHTML = userNameInput
      }
    }
  }, [])

  const dealCards = event => {
    if (deck.piles.userPile.remaining === 26) {
      fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/pile/compPile/draw/?count=26`)
      .then(response => response.json())
      .then(pile => {
        pile.cards.forEach(card => {
          compDiscardStack.innerHTML = `<img src=${card.image}></img>`
        })
      })
      document.getElementById("dealCards").classList.add("hideIt")
      document.getElementById("playACard").classList.remove("hideIt")
      document.getElementById("note").classList.remove("hideIt")
    }
  }

  const drawCard = event => {
    if (deck.piles.userPile.remaining > 0) {
      fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=2`)
      .then(response => response.json())
      .then(data => {

        // User plays card
        if(data.cards){
          userCardPlayed.innerHTML = `<img src=${data.cards[0].image}></img>`
          userCardPlayedValue = getValue(data.cards[0].value)
        }
        deck.remaining -= 1

        // Decide who wins hand
        if(userCardPlayedValue > compCardPlayedValue){
          // User wins
          // Add comp/user cards to users pile
          userDiscardStack.innerHTML = `<img src=${data.cards[0].image}></img>`
          // 
        } else if(userCardPlayedValue < compCardPlayedValue){
            // Computer wins
            console.log("You lose!")
        } else{
            // War!
            console.log("War!")
        }

        // Comp plays new card
        // setTimeout(function(){
        //   compCardPlayed.innerHTML = `<img src=${card.cards[0].image}></img>`
        //   compCardPlayedValue = getValue(card.cards[0].value)
        // }
        // , 2000)
      })
    }else{
      userCardPlayed.innerHTML = "Out of cards"
    }
  }

  return(
    <div>
      <div className="grid-x">

        <div className="cell medium-4 black-bg cell-padding-top">
          <div className="cell medium-12"><img id="ship" src={ship}></img><h3>War: Star Trek Edition</h3></div>
          <div className="cell medium-12">
            <h1>Computer<br></br>vs<br></br><span className="userName"></span></h1>
            <br></br>
            <p><b>Score:</b> &nbsp;&nbsp;&nbsp;&nbsp; Computer: 0 &nbsp;&nbsp;&nbsp;&nbsp; <span className="userName"></span>: 0</p>
            <br></br>
          </div>
          <div className="cell medium-12">
            <button className="button" id="dealCards" onClick={dealCards}>Deal Cards</button>
            <button className="button hideIt" id="playACard">Play a card</button>
            <p><span className="hideIt" id="note">(You can also click on your deck to play a card)</span></p>
          </div>
        </div>

        <div className="cell medium-8 table-bg">
          <div className="grid-x">

            <div className="cell medium-12 margin-bottom"></div>
            <div className="cell medium-12 margin-bottom">
              <div className="grid-x">
                <div className="cell medium-6 card-stack rotate-right" id="comp-card-stack">
                  <img src={backOfCardUpsideDown}></img>
                </div>
                <div className="cell medium-6 card-stack rotate-left" id="comp-discard-stack"></div>
              </div>
            </div>

            <div className="cell medium-12 margin-bottom">
              <div className="grid-x">
                <div className="cell medium-6">
                  <div className="card-played" id="comp-card-played"></div>
                </div>
                <div className="cell medium-6">
                  <div className="card-played" id="user-card-played"></div>
                </div>
              </div>
            </div>

            <div className="cell medium-12 margin-bottom">
              <div className="grid-x">
                <div className="cell medium-6 card-stack rotate-left" id="user-discard-stack"></div>
                <div className="cell medium-6 card-stack rotate-right" id="user-card-stack" onClick={drawCard}>
                  <img src={backOfCard}></img>
                </div>
              </div>
            </div>

          </div>
        </div>

      </div>
    </div>
  )
}

export default Game
