export function shuffleCards() {
  return fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')
    .then(data => data.json())
}

export function drawOneCard() {
  debugger
  if(deck.deck_id){
    return fetch(`https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=1`)
      .then(data => data.json())
  }
}
