        // console.log(deck.cards[i].value)
        return(
          <Card
            key = {deck.cards[i].id}
            id = {deck.cards[i].id}
            value = {deck.cards[i].value}
            suit = {deck.cards[i].suit}
          />
        )
      }
    }
  })

